import React, { Component } from 'react';
import PostItem from './PostItem';
import LoadingOverlay from './LoadingOverlay'
import Comment from "./Comment"

class PostList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      posts: [],
    };
  }

  componentDidMount() {
    this.fetchUsers();
  }

  fetchUsers() {
    this.setState({ loading: true });
    fetch('https://jsonplaceholder.typicode.com/posts')
      .then(response => response.json())
      .then(posts => {
        this.setState({
          loading: false,
          posts
        })
      })
      .catch(e => {
        this.setState({
          loading: false
        });
        alert(e.message)
      })
  }

  render() {
    const { loading, posts} = this.state;
    const { onPostClick, postId } = this.props;
    return (
      <div className='post-list-wrapper'>
      <LoadingOverlay active={loading} />
        { posts.map(post => <PostItem onClick={() => onPostClick(post)} post={post} key={post.id} postId={postId} /> )} 
        <div>{ posts.forEach(post => {
          if (post.id === 1) {
            return(
              <Comment className='comments' postId={postId} />
            )
            }
          })}
        </div>
      </div>
    );
  }
}

export default PostList;
