import React, { Component } from 'react';
import PostList from "./components/PostList";
import AuthorInfo from "./components/AuthorInfo";
import { Button, Grid } from "semantic-ui-react";

class Blog extends Component {
  state = {
    selectedAuthorId: null,
    selectedPostId: null,
    commented: false,
  };

  static getDerivedStateFromError(error) {
    return { hasError: true }
  }

  componentDidCatch(error, errorInfo) {
    console.log(error);
  }

  onSelectPost = post => {
    this.setState
    ({ selectedAuthorId: post.userId,
       selectedPostId: post.id,
       commented: true, 
    })
  };

  render() {
    const { selectedAuthorId, selectedPostId, hasError, commented } = this.state;
    if (hasError) return (
      <Button>Update</Button>
    );
    return (
      <Grid>
        <Grid.Column width={8}>
          <PostList onPostClick={this.onSelectPost} postId={selectedPostId} commented = {commented} />
        </Grid.Column>
        <Grid.Column width={6}>
          <AuthorInfo authorId={selectedAuthorId} />
        </Grid.Column>
      </Grid>
    );
  }
}

export default Blog;
