import React, { Component } from 'react';
import { Feed } from 'semantic-ui-react';
import Comment from './Comment'

class PostItem extends Component {

  constructor(props) {
    super(props);
    this.state = {
      commented: false,
    };
  }

  componentWillMount() {
    const { postId} = this.props;
    this.isCommented(postId);
  }

  isCommented(clickedId) {
    const { post } = this.props;
    if (clickedId === post.id) {
      this.setState = {
        commented: true,
      }
    }
  }

  render() {
    const { post, onClick, postId } = this.props;
    const { commented } = this.state;
    return (
      <Feed>
        <Feed.Event>
          <Feed.Label>
            <img src='https://react.semantic-ui.com/images/avatar/small/justen.jpg' alt='user-avatar'/>
          </Feed.Label>
          <Feed.Content>
            <Feed.Summary onClick={onClick}>
    <Feed.User>{post.title}{post.id}{postId}</Feed.User>
            </Feed.Summary>
            <Feed.Extra text>
              {post.body}
            </Feed.Extra>
          </Feed.Content>
          <Comment postId={postId} commented={commented} />
        </Feed.Event>
      </Feed>
    );
  }
}

export default PostItem;
